    const url = "https://api.pexels.com/v1/search?query=ocean";
    const [products, setProducts] = useState([]);

    const fetchPhotos = () => {
      Axios.get(url, {
        headers: {
          Authorization: apiKey,
        },
      })
        .then((res) => {
          const { data } = res;
          const { photos } = data;
          const allProduct = photos.map((photo) => ({
