          }));
          setProducts(allProduct);
        })
        .catch((error) => {
          console.log(error);
        });
    };
    useEffect(() => {
      fetchPhotos();
    }, []);



    return (

        <Layout>
        <div style={{
            display: 'flex',
            justifyContent: "center",
            textAlign:"center",
            flexDirection: "column",

        }}>
            <h1>Welcome Page </h1>
            {news.map((n,i) => (<p key={i} > {n.title}</p>

            ))}

                {products.map((product) => (<img key={product.id}> </img>
                ))}
        </div>
        </Layout>
    );
};

export default Welcome;
