import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout'






const Welcome = () => {

const [ news, setNews] = useState([])
const [ picture, setPicture ] = useState([])
//fetch news

const fetchNews = () => {
    fetch("http://hn.algolia.com/api/v1/search?query=diatoms")
    .then(result => result.json())
     .then(data => setNews(data.hits))
    //  .then(data => console.log(data));
    // .catch(error => console.log(error));
};
useEffect(() => {
    fetchNews()
})
useEffect(() => {
    document.body.style.backgroundColor = '#4a5f6b'

})

//      PEXELS ----------------------------------------


    const apiKey = "563492ad6f91700001000001dea393468ece41ea8cfa6ab4b59b7913";
    const url = "https://api.pexels.com/v1/search?query=ocean";
    const [products, setProducts] = useState([]);

    const fetchPhotos = () => {
      Axios.get(url, {
        headers: {
          Authorization: apiKey,
        },
      })
        .then((res) => {
          const { data } = res;
          const { photos } = data;
          const allProduct = photos.map((photo) => ({
            smallImage: photo.src.medium,
            tinyImage: photo.src.tiny,
            productName: random.word(),
            productPrice: commerce.price(),
            id: random.uuid(),
          }));
          setProducts(allProduct);
        })
        .catch((error) => {
          console.log(error);
        });
    };
    useEffect(() => {
      fetchPhotos();
    }, []);



    return (

        <Layout>
        <div style={{
            display: 'flex',
            justifyContent: "center",
            textAlign:"center",
            flexDirection: "column",

        }}>
            <h1>Welcome Page </h1>
            {news.map((n,i) => (<p key={i} > {n.title}</p>

            ))}

                {products.map((product) => (<img key={product.id}> </img>
                ))}
        </div>
        </Layout>
    );
};

export default Welcome;
