import React from 'react';
import  { useRouter } from 'next/router'
import Navbar from '../components/navbar'
import Layout from '../components/Layout'
import ReactPlayer from 'react-player/lazy'
import styles from '../styles/Home.module.css'
import CardSmall from '../components/cardSmall'
// import { createClient } from 'pexels';






const About = () => {
    const router = useRouter();


    return (
        <Layout>
            <div className={styles.about}>



            <div style={{ 
                 position: 'absolute',
                 width: "100%",
                 left: "50%",
                 top:"50%",
                 height: "100%",
                 objectFit: "cover",
                 transform: "translate(-50%, -50%)",
                 opacity: '90%',
                 zIndex: '-1',
            }}>
            <ReactPlayer url="https://www.youtube.com/watch?v=nF4SUQU_7cU " controls={true} loop ={true} muted={true} autoplay={true} background={true} playing={true} width='100%' height='100%' left='20%'
            />
            </div>
            <section>
            <div style={{
                display: "flex",
                alignContent:"center",
                padding:"20px",
                flexWrap:"wrap",
            }}>
                <div style={{
                    padding:"50px"}}>
                <h1> We are here to inspire a new geniration of Microbiologist. </h1>
                </div>
                <div style={{
                    position:"relative",
                    bottom:"1%"
                }}>
                <CardSmall />
                {/* <img src="https://2lffqo2moysixpyb349z0bj6-wpengine.netdna-ssl.com/wp-content/uploads/2018/07/kids.jpg" height="200px" width="200px" /> */}
                </div>
            </div>



            </section>


        </div>
        </Layout>
    );
};

export default About;
