//   const res = await fetch(`${YOUTUBE_PLAYLIST_ITEMS_API}?part=snippet&maxResults=20&playlistId=PLEFeVUxfZMzG6S5IbN-GuJKd3841EaqkM&key=${process.env.YOUTUBE_API_KEY}`)
//   const data = await res.json();
//   return {
//     props: {
//       data
//     }
//   }
// }



// WEATHER API 
const [weather,setWeather] = useState([])
  // const APIKEY = `${APIKEY}`
  const  APIKEY = '0b19f325bc0d435df2d6ef551d0c5e32'

  async function fetchData(e) {
    const city = e.target.elements.city.value
    const country = e.target.elements.country.value
      e.preventDefault()
      const apiData = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=${APIKEY}`)
    //const apiData = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&APPID=${APIKEY}`)
      .then( res => res.json())
      .then(data => data)
      if(city && country) {
      setWeather({
        data: apiData,
        city: apiData.city,
        country: apiData.sys.country,
        description: apiData.weather[0].description,
        temperature: apiData.main.temp,
        error:""
      }
      )} else {
        setWeather({
          data: '',
          city: '',
          country: '',
          description: '',
          temperature: '',
          error:"Please Type A City And Country"
      }
      )}
  }




  return (
<Layout>
    <div className={styles.wrapper}>

            <link href="https://fonts.googleapis.com/css2?family=Libre+Caslon+Display&display=swap" rel="stylesheet" />
            {/* <Navbar /> */}
    <div className={styles.container}>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <main>

     <div>
       <Card />
     </div>

    {/* <YouTubePlayList /> */}
    {/* <ul className={styles.grid}>
  {data.items.map(({ id, snippet = {} }) => {
    const { title, thumbnails = {}, resourceId = {} } = snippet;
    const { medium } = thumbnails;

    return (
      <li key={id} className={styles.card}>
        <a href={`https://www.youtube.com/watch?v=${resourceId.videoId}`}>
          <p>
            <img width={medium.width} height={medium.height} src={medium.url} alt="" />
          </p>
          <h3>{ title }</h3>
        </a>
      </li>
    )
  })} */}
{/* </ul> */}

<hr></hr>

 <div className={styles.weatherApp}>
      <h3>WEATHER APP</h3>
      <div className={styles.weatherForm}>
      <Form getWeather={fetchData} />
      </div>
      <div className={styles.weatherData}>
      <Weather
      city={weather.city}
      country={weather.country}
      description={weather.description}
      temperature={weather.temperature}
      error={weather.error}
      /></div>
      {console.log(weather.data)}
    </div> 

    <ReactPlayer url="https://www.youtube.com/watch?v=JZjzQhFG6Ec&t=132s" controls={true} loop ={false} muted={true} autoplay={true} background={true} playing={true} width='50%' height='50%' left='20%'/>

      </main>
</div>

  {/* <NewForm /> */}




<Footer />
  </div>
  </Layout>

  );

}
