import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout'
import Axios from "axios";





const Welcome = () => {

const [ news, setNews] = useState([])

//fetch news

const fetchNews = () => {
    fetch("http://hn.algolia.com/api/v1/search?query=diatoms")
    // fetch("http://hn.algolia.com/api/v1/search?query=foo&tags=story")
    .then(result => result.json())
     .then(data => setNews(data.hits))
    //  .then(data => console.log(data));
    // .catch(error => console.log(error));
};
useEffect(() => {
    fetchNews()
})
useEffect(() => {
    document.body.style.backgroundColor = '#4a5f6b'

})




    return (

        <Layout>
        <div style={{
            display: 'flex',
            justifyContent: "center",
            textAlign:"center",
            flexDirection: "column",

        }}>
            <h1>Welcome Page </h1>
            {news.map((n,i) => (<p key={i} > {n.title}</p>

            ))}
            <div>
            <img src="https://i.pinimg.com/564x/4c/d7/af/4cd7af9b639bbb51f5b278e3ced08f1e.jpg" width="800vw" height="400px" />
            </div>
        </div>
        </Layout>
    );
};

export default Welcome;
